mapper.select:
SELECT P.ID, USERNAME, POSITION, CAREER, M.PROFILE_IMAGE_URL, M.CREATED_AT
FROM MEMBER M
         INNER JOIN PROJECT P ON M.ID = P.OWNER_ID
WHERE P.ID = #{projectId}
  AND P.OWNER_ID = #{ownerId}

SELECT ID
FROM PROJECT
WHERE PROJECT_NAME = #{projectName}

SELECT PROJECT_NAME
FROM PROJECT
WHERE PROJECT_NAME = #{projectName}

mapper.update:
UPDATE PROJECT
SET VIEWS = VIEWS + 1
WHERE ID = #{projectId}

mapper.select:
SELECT STATUS
FROM PROJECT
WHERE ID=#{projectId}

mapper.update:
UPDATE PROJECT
SET STATUS = 0
WHERE ID = #{projectId}

UPDATE PROJECT
SET STATUS = 1
WHERE ID = #{projectId}

mapper.select:
SELECT ID
FROM PROJECT
WHERE ID=#{projectId} AND OWNER_ID=#{memberId}

SELECT M.ID, M.USERNAME, M.POSITION, M.CAREER, M.PROFILE_IMAGE_URL, M.CREATED_AT
FROM SUBSCRIBE S
         INNER JOIN MEMBER M ON S.MEMBER_ID = M.ID
WHERE PROJECT_ID = #{projectId}
  AND MEMBER_ID = #{memberId}

SELECT MEMBER_ID
FROM PROJECT_MEMBER
WHERE PROJECT_ID = #{projectId}

SELECT ID
FROM PROJECT
WHERE STATUS = #{filter}
  AND IS_SHOW = 1
ORDER BY CREATED_AT DESC LIMIT 10
OFFSET #{page}

SELECT ID
FROM PROJECT
WHERE PROJECT_TYPE = #{filter}
  AND IS_SHOW = 1
ORDER BY CREATED_AT DESC LIMIT 10
OFFSET #{page}

SELECT ID, PROJECT_NAME, PROJECT_TYPE, PROJECT_INFO, VIEWS, STATUS, CREATED_AT
FROM PROJECT
WHERE ID = #{projectId}

mapper.update:
UPDATE PROJECT
SET IMAGE_URL=#{image}
WHERE ID = #{projectId}
